mapper.select:
SELECT * FROM account_log 
WHERE mid = #{mid} 
ORDER BY createdAt DESC
LIMIT #{offset}, #{limit}

SELECT * FROM account_log 
WHERE mid = #{mid} AND type = #{type}
ORDER BY createdAt DESC
LIMIT #{offset}, #{limit}

SELECT * FROM account_log 
WHERE mid = #{mid} AND category = #{category}
ORDER BY createdAt DESC
LIMIT #{offset}, #{limit}
