mapper.select:
SELECT sum(num) as c
FROM member_point_logs
WHERE mid = #{mid}

SELECT `day`,`num`,`remark`
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND type = 0
AND num !=0
AND DATE_FORMAT(day, '%Y-%m') = #{yearMonth}
ORDER BY day DESC

SELECT `id`, `day`, `mid`, `uid`, `ruleId`, `num`, `createdAt`, `updatedAt`
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND type = 0
AND day BETWEEN #{startDay} AND #{endDay}
ORDER BY day ASC

SELECT 
    DATE_FORMAT(day, '%Y-%m-%d') as check_date,
    1 as value
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND type = 0
AND num !=0
AND YEAR(day) = #{year}
ORDER BY day ASC

SELECT sum(num)
FROM member_point_logs
WHERE mid = #{mid}  and day = #{day}

SELECT SUM(num) as total
FROM member_point_logs
WHERE mid = #{mid}
    AND day BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(DISTINCT DATE_FORMAT(day, '%Y-%m-%d'))
FROM member_point_logs
WHERE mid = #{mid}
    AND type = 0
    AND day BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(1)
FROM member_point_logs
WHERE mid = #{mid}
AND type = 0
AND day BETWEEN #{startDate} AND #{endDate}

SELECT SUM(num) as total
FROM member_point_logs
WHERE mid = #{mid}
    AND type = 1
    AND day BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(DISTINCT DATE(day)) as count
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND type = 0
AND num > 0
AND day BETWEEN #{startDate} AND #{endDate}
