mapper.select:
SELECT `day`,`num`,`remark`
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND type in (0,5)
AND num !=0
AND DATE_FORMAT(day, '%Y-%m') = #{yearMonth}
ORDER BY day DESC

SELECT *
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND type in (0,5)
AND day BETWEEN #{startDay} AND #{endDay}
ORDER BY day ASC

SELECT 
    DATE_FORMAT(day, '%Y-%m-%d') as check_date,
    1 as value
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND type in (0,5)
AND num !=0
AND YEAR(day) = #{year}
ORDER BY day ASC

SELECT sum(num)
FROM member_point_logs
WHERE mid = #{mid}  and day = #{day}

SELECT COUNT(DISTINCT DATE_FORMAT(day, '%Y-%m-%d'))
FROM member_point_logs
WHERE mid = #{mid}
    AND type in (0,5)
    AND day BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(1)
FROM member_point_logs
WHERE mid = #{mid}
AND type in (0,5)
AND day BETWEEN #{startDate} AND #{endDate}

SELECT SUM(num) as total
FROM member_point_logs
WHERE mid = #{mid}
    AND type = 1
    AND day BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(DISTINCT DATE(day)) as count
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND type in (0,5)
AND num > 0
AND day BETWEEN #{startDate} AND #{endDate}

SELECT IFNULL(SUM(num), 0)
FROM member_point_logs
WHERE mid = #{mid} 
AND ruleId = #{ruleId}
AND day BETWEEN #{startDate} AND #{endDate}
AND status = 0
AND type IN (0, 5)

SELECT IFNULL(SUM(ABS(num)), 0)
FROM member_point_logs
WHERE mid = #{mid} 
AND day BETWEEN #{startDate} AND #{endDate}
AND num < 0
AND status = 0
