mapper.select:
SELECT 
FROM season_rule_achievements
WHERE id = #{id} AND seasonId = #{seasonId}

mapper.insert:
INSERT INTO season_rule_achievements (
    seasonId, ruleId, title, img, conditionType, conditionValue, rewardType, rewardValue, status
) 
VALUES (
    #{seasonId}, #{ruleId}, #{title}, #{img}, #{conditionType}, #{conditionValue}, #{rewardType}, #{rewardValue}, #{status}
)

mapper.update:
UPDATE season_rule_achievements
ruleId = #{ruleId}, title = #{title}, img = #{img}, conditionType = #{conditionType}, conditionValue = #{conditionValue}, rewardType = #{rewardType}, rewardValue = #{rewardValue}, status = #{status}, updatedAt = CURRENT_TIMESTAMP
WHERE id = #{id} AND seasonId = #{seasonId}
