mapper.update:
UPDATE season_rules
name = #{name}, type = #{type}, typeSort = #{typeSort}, icon = #{icon}, iconType = #{iconType}, weeks = #{weeks}, content = #{content}, sort = #{sort}, status = #{status}, quickScore = #{quickScore}, enablePomodoro = #{enablePomodoro}, pomodoroTime = #{pomodoroTime}, isAchievement = #{isAchievement}, completionConditions = #{completionConditions}, isTimerRule = #{isTimerRule}, updateTime = now()
WHERE id = #{id}

UPDATE season_rules
SET sort = #{sort}, updateTime = now()
WHERE id = #{id}

UPDATE season_rules
SET type = #{newType}, updateTime = now()
WHERE mId = #{mId} AND seasonId = #{seasonId} AND type = #{oldType}
