mapper.select:
SELECT COUNT(*)
FROM season_wishes
WHERE seasonId = #{seasonId} AND mid = #{mid}

mapper.insert:
INSERT INTO season_wishes (
    seasonId, mid, img, title, content, needPoint, unit, unitType, type, status, createdAt, updatedAt
) 
VALUES (
    #{seasonId}, #{mid}, #{img}, #{title}, #{content}, #{needPoint}, #{unit}, #{unitType}, #{type}, #{status}, now(), now()
)

mapper.update:
UPDATE season_wishes
img = #{img}, title = #{title}, content = #{content}, needPoint = #{needPoint}, unit = #{unit}, unitType = #{unitType}, type = #{type}, status = #{status}, updatedAt = now()
WHERE id = #{id}

UPDATE season_wishes
SET status = #{status}, updatedAt = now()
WHERE id = #{id}
