mapper.update:
UPDATE wish_log 
SET status = #{status}
, endTime = #{endTime}
WHERE id = #{id}

mapper.select:
SELECT COUNT(t1.id)
FROM wish_log as t1, wish as t2
WHERE t1.wid = t2.id AND t2.mid = #{mid}

    AND t1.status = #{status}

SELECT t1.id,t1.wid,t1.point,t1.status,t2.img,t2.title,t2.content,t2.needPoint,t2.unit,t2.unitType,
       date_format(t1.createdAt, '%Y-%m-%d %H:%i:%s') as updatedAt,t1.amount
FROM wish_log as t1, wish as t2
WHERE t1.wid = t2.id AND t2.mid = #{mid}

    AND t1.status = #{status}

ORDER BY t1.createdAt DESC
LIMIT #{offset}, #{pageSize}

        SELECT COUNT(*)
        FROM wish_log
        WHERE mid = #{mid}
--         AND status IN (0, 1, 2) 
        AND createdAt BETWEEN #{startDate} AND #{endDate}

        SELECT SUM(point)
        FROM wish_log
        WHERE mid = #{mid}
--         AND status IN (0, 1, 2) 
        AND createdAt BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(*)
FROM wish_log
WHERE wid = #{id}
AND createdAt   >=  #{startTime}
